          <meshPhongMaterial
            attachArray="material"
            color={props.color.color}
            shininess={props.color.shininess}
          />
        )}
      </mesh>
    </group>
  );
}

function Back2({ ...props }) {
  const group = useRef();
  const name = "BK2";
  const { nodes, materials } = useGLTF("/back2.glb");
  return (
    <group name={name} ref={group} {...props} dispose={null} scale={0.5}
