import "./Home.css";
import * as THREE from "three";
import { Suspense, useRef, useState } from "react";
import { saveAs } from "file-saver";
import { Canvas, useThree, useLoader } from "@react-three/fiber";
import { OrbitControls, useGLTF, useTexture } from "@react-three/drei";
import { TextureLoader } from "three/src/loaders/TextureLoader";

//import Swatch components
import { RightHand } from "../objects/RightHand/RightHand";
import { LeftHand } from "../objects/LeftHand/LeftHand";
import { Body } from "../objects/Body/Body";
import { Option } from "../options/ActiveOption";
import { Color } from "../colors/Color";
import { GLTFExporter } from "../exporter/GLTFExporter";
import { Button } from "@material-ui/core";

function Support({ ...props }) {
  const group = useRef();
  const name = "SP";
  const { nodes, materials } = useGLTF("/support.glb");
  return (
    <group name={name} ref={group} {...props} dispose={null}>
      <mesh
        geometry={nodes.support.geometry}
        material={materials.aiStandardSurface1}
      >
        {props.texture !== null && (
          <meshPhongMaterial
            attachArray="material"
            map={props.texture.map}
            shininess={props.texture.shininess}
          />
        )}
        {props.color != null && (
          <meshPhongMaterial
            attachArray="material"
            color={props.color.color}
            shininess={props.color.shininess}
          />
        )}
      </mesh>
    </group>
  );
}
function Back({ ...props }) {
  const group = useRef();
  const name = "BK";
  const { nodes, materials } = useGLTF("/back.glb");
  return (
    <group name={name} ref={group} {...props} dispose={null}>
      <mesh
        geometry={nodes.back.geometry}
        material={materials.aiStandardSurface1}
      >
        {props.texture !== null && (
          <meshPhongMaterial
            attachArray="material"
            map={props.texture.map}
            shininess={props.texture.shininess}
          />
        )}
        {props.color != null && (
          <meshPhongMaterial
            attachArray="material"
            color={props.color.color}
            shininess={props.color.shininess}
          />
        )}
      </mesh>
    </group>
  );
}
function Back1({ ...props }) {
  const group = useRef();
  const name = "BK";
  const { nodes, materials } = useGLTF("/back1.glb");
  return (
    <group name={name} ref={group} {...props} dispose={null}>
      <mesh
        geometry={nodes.back1.geometry}
        material={materials.aiStandardSurface1}
      >
        {props.texture !== null && (
          <meshPhongMaterial
            attachArray="material"
            map={props.texture.map}
            shininess={props.texture.shininess}
          />
        )}
        {props.color != null && (
          <meshPhongMaterial
            attachArray="material"
            color={props.color.color}
            shininess={props.color.shininess}
          />
        )}
      </mesh>
    </group>
  );
}

